import React, { createContext, useContext, useEffect, useState } from 'react';
import { ErrorMessage, Formik } from 'formik';
import { Form } from 'react-bootstrap';
import * as yup from 'yup';
import './login.scss';
import AuthService from '../../services/auth.service';
import Error from '../../components/error/error.component';

const AuthContext = createContext({
  authenticated: undefined,
  user: {
    email: null,
  },
});

export default function LoginPage() {
  const service = new AuthService();
  const [users, setUsers] = useState({ email: '', password: '' });

  useEffect(() => {
    service.getUser(users?.email, users?.password).then((res) => {
      setUsers(res.data);
    });
  }, []);

  const validationSchema = yup.object().shape({
    email: yup.string().required('blahblah').min(3, 'At least 3 characters'),
    password: yup.string().required('Required'),
  });


  return (
    <>
      <h1>Departure - dein Dashboard für den öffentlichen Verkehr.</h1>

      <Formik
        initialValues={{ email: '', password: '' }}
        validationSchema={validationSchema}
        onSubmit={(event) => {
          event.preventDefault();
          setUsers({ email: event.email, password: event.password });
          console.log(users);
        }}
      >
          <Form>
            <div className='Login'>
              <div className='form-group'>
                <label>Email address</label>
                <input
                  type='email'
                  className='form-control'
                  id='email'
                  aria-describedby='emailHelp'
                  placeholder='Enter email'
                />
                <ErrorMessage name='email'>{(msg) => <Error message={msg}></Error>}</ErrorMessage>
              </div>
              <div className='form-group'>
                <label>Password</label>
                <input
                  type='password'
                  className='form-control'
                  id='password'
                  placeholder='Password'
                />
                <ErrorMessage name='password'>
                  {(msg) => <Error message={msg}></Error>}
                </ErrorMessage>
              </div>
              <button type='submit' className='btn btn-primary' disabled={isSubmitting}>
                Submit
              </button>
            </div>
          </Form>

      </Formik>
    </>
  );
}

export function Login() {
  const [user, setUser] = useState({
    authenticated: false,
    user: {
      email: undefined,
    },
  });

  function login() {
    setUser({
      authenticated: true,
      user: {
        email: 'example@example.com',
      },
    });
  }

  function logout() {
    setUser({
      authenticated: false,
      user: {
        email: null,
      },
    });
  }
  return (
    <>
      <button onClick={login}>login</button>
      <AuthContext.Provider value={user}>{`Hello ${user.user.email}`}</AuthContext.Provider>
      <Child />
      {user.authenticated && <button onClick={logout}>logout</button>}
    </>
  );
}
export function Child() {
  const user = useContext(AuthContext);

  useEffect(() => {
    console.log('changes');
  }, [user]);

  return user && <p>{`Authenticated: ${user.authenticated}`}</p>;
}
